import { createContext, useEffect, useState } from "react";
import { PRODUCTS } from "../products";

export const ShopContext = createContext(null);

const getDefaultCart = () => {
  let cart = {};
  for (let i = 1; i < PRODUCTS.length + 1; i++) {
    cart[i] = 0;
  }
  return cart;
};

export const ShopContextProvider = (props) => {
  const [cartItems, setCartItems] = useState(getDefaultCart());

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInfo = PRODUCTS.find((product) => product.id === Number(item));
        totalAmount += cartItems[item] * itemInfo.price;
      }
    }
    return totalAmount;
  };

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
  };

  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  };

  const updateCartItemCount = (newAmount, itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: newAmount }));
  };

  const checkout = () => {
    setCartItems(getDefaultCart());
  };

  const reserveOrder = () => {
    const reservedOrder = {
      id: Date.now(),
      items: cartItems,
      date: new Date().toISOString(),
      status: "Reserved",
    };
  
    console.log("Order Reserved:", reservedOrder);
  
    // Clear the cart after reservation
    setCartItems(getDefaultCart());
  };
  
  // Add the "Buy Now" functionality
  const buyNow = (itemId) => {
    const item = PRODUCTS.find((product) => product.id === itemId);
    if (item) {
      alert(`You have purchased: ${item.productName} for $${item.price}`);
      // Implement additional purchase logic if needed (e.g., redirection to checkout)
    }
  };
   
  const contextValue = {
    cartItems,
    addToCart,
    updateCartItemCount,
    removeFromCart,
    getTotalCartAmount,
    checkout,
    reserveOrder,
    buyNow  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};
