import { createContext, useState } from "react";
import { PRODUCTS } from "../products";
import axios from "axios"; // Import axios để giao tiếp với backend

export const ShopContext = createContext(null);

const getDefaultCart = () => {
  let cart = {};
  for (let i = 1; i < PRODUCTS.length + 1; i++) {
    cart[i] = 0;
  }
  return cart;
};

export const ShopContextProvider = (props) => {
  const [cartItems, setCartItems] = useState(getDefaultCart());
  const [orderStatus, setOrderStatus] = useState({ loading: false, success: false, error: null }); // Trạng thái đặt hàng

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInfo = PRODUCTS.find((product) => product.id === Number(item));
        totalAmount += cartItems[item] * itemInfo.price;
      }
    }
    return totalAmount;
  };

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
  };

  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  };

  const updateCartItemCount = (newAmount, itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: newAmount }));
  };

  const checkout = () => {
    setCartItems(getDefaultCart());
  };

  const reserveOrder = async () => {
    setOrderStatus({ loading: true, success: false, error: null }); // Bắt đầu trạng thái loading

    try {
      const reservedItems = Object.keys(cartItems)
        .filter((itemId) => cartItems[itemId] > 0)
        .map((itemId) => ({
          productId: itemId,
          quantity: cartItems[itemId],
        }));

      const orderData = {
        items: reservedItems,
        totalAmount: getTotalCartAmount(),
      };

      // Gửi yêu cầu đến backend
      const response = await axios.post("http://localhost:5000/api/orders", orderData, {
        headers: {
          Authorization: localStorage.getItem("token"),
        },
      });

      console.log("Order Reserved:", response.data);
      setOrderStatus({ loading: false, success: true, error: null }); // Đặt hàng thành công

      // Xóa giỏ hàng sau khi đặt hàng
      setCartItems(getDefaultCart());
    } catch (err) {
      console.error("Error reserving order:", err);
      setOrderStatus({ loading: false, success: false, error: err.message }); // Xử lý lỗi
    }
  };

  const contextValue = {
    cartItems,
    addToCart,
    updateCartItemCount,
    removeFromCart,
    getTotalCartAmount,
    checkout,
    reserveOrder,
    orderStatus,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};
